import numpy as npfrom scipy.integrate import odeintimport matplotlib.pyplot as plt# Define the Queue Length ODE functiondef queueLength(Q, t, arrival_rate, processing_rate):    dQdt = arrival_rate - processing_rate * Q    return dQdt# Prompt the user for input or use defaultsprint("Queue Length ODE Solver")# Prompt the user for arrival rate (tasks per unit time)arrival_rate_input = input("Enter the arrival rate (tasks per unit time, e.g., 2.0), or press Enter to use the default (2.0): ")if arrival_rate_input:    arrival_rate = float(arrival_rate_input)else:    arrival_rate = 2.0  # Default arrival rate# Prompt the user for processing rate (tasks per unit time)processing_rate_input = input("Enter the processing rate (tasks per unit time, e.g., 1.0), or press Enter to use the default (1.0): ")if processing_rate_input:    processing_rate = float(processing_rate_input)else:    processing_rate = 1.0  # Default processing rate# Time points for the solutiont = np.linspace(0, 10, 100)  # Example time range from 0 to 10 units# Solve the Queue Length ODEinitial_queue_length = 0  # Initial queue lengthsolution = odeint(queueLength, initial_queue_length, t, args=(arrival_rate, processing_rate))# Visualize the solutionplt.figure(figsize=(8, 6))plt.plot(t, solution, label='Queue Length')plt.xlabel('Time')plt.ylabel('Queue Length')plt.title('Queue Length over Time')plt.legend()plt.grid(True)plt.show()